import React, { useContext, useState, useEffect } from 'react';
import { Container, Row, Col } from 'reactstrap';
import NewsLetter from '../shared/Newsletter';
import FeaturedTourList from '../components/Featured-tours/FeaturedTourList';
import Subtitle from '../shared/Subtitle';
import { BASE_URL } from '../utils/config';
import { AuthContext } from '../context/AuthContext';



const BookingList = () => {
const {user} = useContext(AuthContext)
const [bookings, setBookings] = useState([]);



    console.log('User id : ',user.data._id)


  useEffect(() => {
    const fetchBookings = async () => {
        try {
            const response = await fetch(`${BASE_URL}/booking/${user.data._id}`);
            console.log('Here is the response', response);
            if (!response.ok) {
                throw new Error('Failed to fetch bookings');
            }
            const data = await response.json();
            setBookings(data.data);
            console.log('here is the data', data.data);          
        } catch (error) {
            console.error('Error fetching bookings:', error);
        }
    };
    console.log('bookings',bookings.length)

    fetchBookings();
}, [user.data._id]); // Empty dependency array

return (
    <section>
      <h2>Bookings for User ID: {user.data._id}</h2>
      <ul>
      {bookings.length > 0 ? (
                    bookings.map((booking) => (
                        <li key={booking._id}>{booking.data}</li> // Adjust this line according to your data structure
                    ))
                ) : (
                    <li>No bookings found</li>
                )}
      </ul>
    </section>
  );

  
};

const Dashboard = () => {
  return <>
    {/* <section>
      <Container>
        <Row>
          <Col lg='6'>
            <Subtitle subtitle={'Booked Tours'} />
          <BookingList/>
          </Col>
        </Row>
      </Container>
    </section> */}

    <section>
    <Container>
        <Row>
          <Col lg='12' className='mb-5'>
            <BookingList/>
            <Subtitle subtitle={'Recommendations'} />
            <h2 className='featured__tour-title'>Our Best Featured Tours</h2>
          </Col>
          <FeaturedTourList />
        </Row>
      </Container>

      <NewsLetter />
    </section>



    

    
    </>
};

export default Dashboard;
