import React, { useContext, useState, useEffect } from 'react';
import { Container, Row, Col ,Form} from 'reactstrap';
import NewsLetter from '../shared/Newsletter';
import FeaturedTourList from '../components/Featured-tours/FeaturedTourList';
import Subtitle from '../shared/Subtitle';
import { BASE_URL } from '../utils/config';
import { AuthContext } from '../context/AuthContext';



const BookingList = () => {
const {user} = useContext(AuthContext)
const [bookings, setBookings] = useState([]);


  useEffect(() => {
    const fetchBookings = async () => {
        try {
            const response = await fetch(`${BASE_URL}/booking/${user.data._id}`);
            if (!response.ok) {
                throw new Error('Failed to fetch bookings');
            }
            const data = await response.json();
            setBookings(data.data);
            console.log('Data : ',data.data)

        } catch (error) {
            console.error('Error fetching bookings:', error);
        }
    };
    fetchBookings();
}, [user.data._id]); 

return (
    <section>
      <Subtitle subtitle={'My Bookings'} />
     
      {bookings.length > 0 ? (
                    bookings.map((booking) => (
                      <Form key={booking._id}>
                      <input type="text" value={booking.userEmail} disabled />
                      <input type="text" value={booking.fullName} disabled />
                      <Input type="text" value={booking.tourName} disabled />
                      <Input type="number" value={booking.passengerCount} disabled />
                      <Input type="date" value={booking.tourDate} disabled />
                      <Button color="primary" >Update</Button>
                      <Button color="danger" >Cancel</Button>
                  </Form>
                    ))
                ) : (
                  <p>No bookings found</p>
                )}
      
    </section>
  );

  
};

const Dashboard = () => {
  return <>

    <section>
    <Container>
        <Row>
          <Col lg='12' className='mb-5'>
            <BookingList/>
            <Subtitle subtitle={'Recommendations'} />
            <h2 className='featured__tour-title'>Our Best Featured Tours</h2>
          </Col>
          <FeaturedTourList />
        </Row>
      </Container>

      <NewsLetter />
    </section>



    

    
    </>
};

export default Dashboard;
