import React, { useState, useEffect } from 'react';
import './../styles/admin-dashboard.css';

function AdminDashboard() {
  const [tours, setTours] = useState([]);
  const [formData, setFormData] = useState({
    title: '',
    city: '',
    address: '',
    distance: '',
    photo: '',
    desc: '',
    price: '',
    maxGroupSize: '',
  });

  // Fetch tours when component mounts
  useEffect(() => {
    // Add code to fetch tours from backend
    // Example: fetchTours();
  }, []);

  // Function to handle form field changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  // Function to handle form submission (create or update tour)
  const handleSubmit = (e) => {
    e.preventDefault();
    // Add code to send formData to backend for creation/update
    // Example: createOrUpdateTour(formData);
  };

  // Function to handle tour deletion
  const handleDeleteTour = (tourId) => {
    // Add code to send delete request to backend
    // Example: deleteTour(tourId);
  };

  return (
    <div className="admin-dashboard">
      <h2>Create/Update Tour</h2>
      <form onSubmit={handleSubmit}>
        {/* Form fields */}
        <button type="submit">Submit</button>
      </form>

      <h2>Tours</h2>
      <ul>
        {tours.map((tour) => (
          <li key={tour._id}>
            {tour.title} - {tour.city}
            <button onClick={() => handleDeleteTour(tour._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default AdminDashboard;
