import { React ,useContext, useEffect,useState }from "react";
import { AuthContext } from "../context/AuthContext";
import { Container, Row, Col } from 'reactstrap';
import { useNavigate } from "react-router-dom";
import './../styles/admin-dashboard.css'
import Subtitle from '../shared/Subtitle';
import { BASE_URL } from './../utils/config';
import CreateForm from "../Form/CreateForm";
import UpdateForm from "../Form/UpdateForm";



const AdminDashboard = () => {
const {user} = useContext(AuthContext)
const navigate=useNavigate();

  const [userCount, setUserCount] = useState(0);
  const [tourCount, setTourCount] = useState(0);
  const [bookingCount, setBookingCount] = useState(0);
  const [reviewCount, setReviewCount] = useState(0);

useEffect(() => {
    move();
    fetchCounts();
});

const fetchCounts = async () => {
    try {
      //Fetch user count
      const res = await fetch(`${BASE_URL}/users`)
      const result = await res.json()
      setUserCount(result.count);    

      //Fetch tour count
      const toursResponse = await fetch(`${BASE_URL}/tours/search/getTourCount`);
      const toursData = await toursResponse.json();
      setTourCount(toursData.data);

      //Fetch bookings count //Fetch tour count
      const bookResponse = await fetch(`${BASE_URL}/booking`);
      const bookData = await bookResponse.json();
      setBookingCount(bookData.count);

      //Fetch review count //Fetch tour count
      const reviewResponse = await fetch(`${BASE_URL}/review`);
      const reviewData = await reviewResponse.json();
      setReviewCount(reviewData.count);


      
    } catch (error) {
      console.error('Error fetching counts:', error);
    }
  };

  const [showCreateForm, setShowCreateForm] = useState(false);
  const [showUpdateForm, setShowUpdateForm] = useState(false);
  const [showDeleteForm, setShowDeleteForm] = useState(false);

  const handleCreateClick = () => {
    setShowCreateForm(!showCreateForm);
    setShowUpdateForm(false);
    setShowDeleteForm(false);
  };

  const handleUpdateClick = () => {
    setShowCreateForm(false);
    setShowUpdateForm(!showUpdateForm);
    setShowDeleteForm(false);
  };

  const handleDeleteClick = () => {
    setShowCreateForm(false);
    setShowUpdateForm(false);
    setShowDeleteForm(!showDeleteForm);
  };

const move = () => {
    if (user && user.role === "user") {
        navigate("/");
    }    
}

    return <>


<Container>
                <Row>
                    <Col lg='12'>
                        <div className="experience__content">
                            <Subtitle subtitle={'User and Tour Data'}/>
                            
                        </div>

                        <div className="counter__wrapper d-flex align-items-center gap-5">
                            <div className="counter__box">
                                <span>
                                    {userCount}
                                </span>
                                <h6>Total number Of Users</h6>
                            </div>

                            <div className="counter__box">
                                <span>
                                    {tourCount}
                                </span>
                                <h6>Total number of Tours</h6>
                            </div>
                            <div className="counter__box">
                                <span>
                                    {bookingCount}
                                </span>
                                <h6>Total number of Bookings</h6>
                            </div>
                            <div className="counter__box">
                                <span>
                                    {reviewCount}
                                </span>
                                <h6>Total number of Reviews</h6>
                            </div>
                        </div>
                    </Col>                   
                </Row>
            </Container>


            <section>
                <Container>
                    <Row>
                        <Col>
                        <div>
      <table>
        <thead>
          <tr>
            <th>Title</th>
            <th>City</th>
            <th>Address</th>
            <th>Distance</th>
            <th>Photo</th>
            <th>Description</th>
            <th>Price</th>
            <th>Max Group Size</th>
          </tr>
        </thead>
        <tbody>
          {tours.map(tour => (
            <tr key={tour._id}>
              <td>{tour.title}</td>
              <td>{tour.city}</td>
              <td>{tour.address}</td>
              <td>{tour.distance}</td>
              <td><img src={tour.photo} alt={tour.title} style={{ width: '100px', height: 'auto' }} /></td>
              <td>{tour.desc}</td>
              <td>{tour.price}</td>
              <td>{tour.maxGroupSize}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={handlePrevPage} disabled={currentPage === 1}>Previous</button>
      <button onClick={handleNextPage}>Next</button>
    </div>
                        </Col>
                    </Row>
                </Container>
            </section>

            



        <Container>
            <Row>
                <Col>
                <div className="buttons">
                    <button onClick={handleCreateClick}>Create Tour</button>
                    <button onClick={handleUpdateClick}>Update Tour</button>
                    <button onClick={handleDeleteClick}>Delete Tour</button>
                </div>
                </Col>
            </Row>
        </Container>


        <Container>
                <Row>
                    <Col>
                        <div className="booking__form">
                            {showCreateForm && <CreateForm/>}
                            {showUpdateForm && <UpdateForm/>}
                            {showDeleteForm && <div className="form-container">Delete Tour Form</div>}
                        </div>
                    </Col>
                </Row>
            </Container>

            <Container>
                <Row>
                    <Col>
                        <h1>Hello</h1>
                    </Col>
                </Row>
            </Container>
    </>
}

export default AdminDashboard;
