import React, { useContext, useState, useEffect } from 'react';
import { Container, Row, Col } from 'reactstrap';
import NewsLetter from '../shared/Newsletter';
import FeaturedTourList from '../components/Featured-tours/FeaturedTourList';
import Subtitle from '../shared/Subtitle';
import { BASE_URL } from '../utils/config';
import { AuthContext } from '../context/AuthContext';



const BookingList = () => {
const {user} = useContext(AuthContext)
const [bookings, setBookings] = useState([]);

    console.log('User id : ',user.data._id)


  useEffect(() => {
    const fetchBookings = async () => {
        try {
            const response = await fetch(`${BASE_URL}/booking/${user.data._id}`);
            console.log('Here is the response', response);
            if (!response.ok) {
                throw new Error('Failed to fetch bookings');
            }
            const data = await response.json();
            console.log('here is the data', data);
        } catch (error) {
            console.error('Error fetching bookings:', error);
        }
    };

    fetchBookings();
}, [user.data._id]); // Empty dependency array


  
};

const Dashboard = () => {
  return <>
    <section>
      <Container>
        <Row>
          <Col lg='6'>
          <BookingList/>
          <ul>
              {bookings.map((booking) => (
                <li key={booking._id}>{/* Render booking details */}</li>
              ))}
            </ul>
          </Col>
        </Row>
      </Container>
    </section>

    <section>
    <Container>
        <Row>
          <Col lg='12' className='mb-5'>
            <Subtitle subtitle={'Recommendations'} />
            <h2 className='featured__tour-title'>Our Best Featured Tours</h2>
          </Col>
          <FeaturedTourList />
        </Row>
      </Container>

      <NewsLetter />
    </section>



    

    
    </>
};

export default Dashboard;
