import { React ,useContext, useEffect,useState }from "react";
import { AuthContext } from "../context/AuthContext";
import { Container, Row, Col } from 'reactstrap';
import { useNavigate } from "react-router-dom";
import './../styles/admin-dashboard.css'
import Subtitle from '../shared/Subtitle';
import { BASE_URL } from './../utils/config';



const AdminDashboard = () => {
const {user} = useContext(AuthContext)
const navigate=useNavigate();

const [userCount, setUserCount] = useState(0);
  const [tourCount, setTourCount] = useState(0);
  const [bookingCount, setBookingCount] = useState(0);
  const [reviewCount, setReviewCount] = useState(0);

useEffect(() => {
    move();
    fetchCounts();
});

const fetchCounts = async () => {
    try {
      //Fetch user count
      const res = await fetch(`${BASE_URL}/user/count`,{})

    const result = await res.json()
    setUserCount(result.count);

    

      //Fetch tour count
      const toursResponse = await fetch('/api/tours/count');
      const toursData = await toursResponse.json();
      setTourCount(toursData.count);

      
    } catch (error) {
      console.error('Error fetching counts:', error);
    }
  };


const move = () => {
    if (user && user.role === "user") {
        navigate("/");
    }
    console.log(user);
    
}

    return <>


<Container>
                <Row>
                    <Col lg='12'>
                        <div className="experience__content">
                            <Subtitle subtitle={'User and Tour Data'}/>
                            
                        </div>

                        <div className="counter__wrapper d-flex align-items-center gap-5">
                            <div className="counter__box">
                                <span>
                                    {userCount}
                                </span>
                                <h6>Total number Of Users</h6>
                            </div>

                            <div className="counter__box">
                                <span>
                                    2k+
                                </span>
                                <h6>Total number of Tours</h6>
                            </div>
                            <div className="counter__box">
                                <span>
                                    15
                                </span>
                                <h6>Total number of Bookings</h6>
                            </div>
                            <div className="counter__box">
                                <span>
                                    15
                                </span>
                                <h6>Total number of Reviews</h6>
                            </div>
                        </div>
                    </Col>
                    
                </Row>
            </Container>



        <Container>
            <Row>
                <Col>
                <div className="buttons">
                    <button >Create Tour</button>
                    <button >Update Tour</button>
                    <button >Delete Tour</button>
                </div>
                </Col>
            </Row>
        </Container>
    </>
}

export default AdminDashboard;
