import React, { useContext, useState, useEffect } from 'react';
import moment from 'moment'; 
import { Container, Row, Col ,Form} from 'reactstrap';
import NewsLetter from '../shared/Newsletter';
import FeaturedTourList from '../components/Featured-tours/FeaturedTourList';
import Subtitle from '../shared/Subtitle';
import { BASE_URL } from '../utils/config';
import { AuthContext } from '../context/AuthContext';



const BookingList = () => {
const {user} = useContext(AuthContext)
const [bookings, setBookings] = useState([]);


  useEffect(() => {
    const fetchBookings = async () => {
        try {
            const response = await fetch(`${BASE_URL}/booking/${user.data._id}`);
            if (!response.ok) {
                throw new Error('Failed to fetch bookings');
            }
            const data = await response.json();
            setBookings(data.data);
            console.log('Data : ',data.data)

        } catch (error) {
            console.error('Error fetching bookings:', error);
        }
    };
    fetchBookings();
}, [user.data._id]); 

return (
    <section>
      <Container>
        
      <Subtitle subtitle={'My Bookings'} />
     
      {bookings.length > 0 ? (
                    bookings.map((booking) => (
                      <div className="booking__form">
                      <Form key={booking._id}>
                      <input type="text" value={booking.userEmail} disabled />
                      <input type="text" value={booking.fullName} disabled />
                      <input type="text" value={booking.tourName} disabled />
                      <input type="number" value={booking.guestSize} disabled />
                      <input type="date" value={moment(booking.bookAt).format('YYYY-MM-DD')} disabled />
                      <button className='btn booking__btn' >Update</button>
                      <button color="danger" >Cancel</button>
                  </Form>
                  </div>
                    ))
                ) : (
                  <p>No bookings found</p>
                )}
      </Container>
      
    </section>
  );

  
};

const Dashboard = () => {
  return <>

    <section>
    <Container>
        <Row>
          <Col lg='12' className='mb-5'>
            <BookingList/>
            <Subtitle subtitle={'Recommendations'} />
            <h2 className='featured__tour-title'>Our Best Featured Tours</h2>
          </Col>
          <FeaturedTourList />
        </Row>
      </Container>

      <NewsLetter />
    </section>



    

    
    </>
};

export default Dashboard;
