import React, { useState } from 'react';
import { Container, Row, Col } from 'reactstrap';


function UploadForm() {
    const [image,setImage]=useState('')
    const [formData, setFormData] = useState({
        title: '',
        city: '',
        address: '',
        distance: '',
        price: '',
        maxGroupSize: '',
        desc: '',
        photo: null,
        featured: false,
      }); 

    function convertToBase64(e){
        const file = e.target.files[0];
        const reader = new FileReader();
        reader.readAsDataURL(file);
        reader.onload = () => {
            const base64Result = reader.result;
            console.log(base64Result);
            setImage(base64Result);
            setFormData((prevData) => ({
                ...prevData,
                photo: base64Result,
              }));
        };
        reader.onerror = error => {
            console.log("Error: ", error);
        };
    }

    return <>
        <Container>
            <Row>
                <Col>
                <form onSubmit={handleSubmit}>
                    <div className='form-main-group'>
                    <div className="file-input-container">
                        <input type="file" id="photo" name="photo" onChange={convertToBase64} />
                        {image==='' || image===null ?"":<img width={100} height={100} src={image} alt=''/>}
                    </div>

                        <div className="form-group">
                        <input type="text" placeholder='Title' name="title" value={formData.title} onChange={handleChange} />
                        </div>
                        
                        <button className='search__btn' type="submit">Create</button>
                        </div>
                </form>
                </Col>
            </Row>
        </Container>
    
    </>
}

export default UploadForm;
