import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { BASE_URL } from '../utils/config'; 
import './GenreImages.css'; // Import CSS file for styling

const GenreImages = () => {
    const { genre } = useParams();
    const [images, setImages] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchImages = async () => {
            setLoading(true);
            try {
                const response = await fetch(`${BASE_URL}/gallery?genre=${genre}`);
                if (!response.ok) {
                    throw new Error('Failed to fetch data');
                }
                const responseData = await response.json();
                const { data } = responseData;
                setImages(data);
                setLoading(false);
            } catch (error) {
                setError(error.message);
                setLoading(false);
            }
        };
        
        fetchImages();
    }, [genre]);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div className="container">
            <h2>Images from {genre}</h2>
            <div className="row">
                {images.map((image, index) => (
                    <div className="col-md-3 mb-3" key={index}>
                        <div className="image-container">
                            <img src={image.photo} alt={image.title} className="img-fluid hover-effect" />
                            <div className="image-title">{image.title}</div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default GenreImages;
