import React, { useContext, useState, useEffect } from 'react';
import { Container, Row, Col } from 'reactstrap';
import NewsLetter from '../shared/Newsletter';
import FeaturedTourList from '../components/Featured-tours/FeaturedTourList';
import Subtitle from '../shared/Subtitle';
import { BASE_URL } from '../utils/config';
import { AuthContext } from '../context/AuthContext';

const BookingList = ({ bookings }) => {
  // Render the list of bookings using the 'bookings' prop
  return (
    <section>
      <Container>
        <Row>
          <Col lg='6'>
            <Subtitle subtitle={'Booked Tours'} />
            {/* Render the list of bookings here */}
            
          </Col>
        </Row>
      </Container>
    </section>
  );
};

const Dashboard = () => {
  const { user } = useContext(AuthContext);
  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const response = await fetch(`${BASE_URL}/booking/${user.data._id}`);
        if (!response.ok) {
          throw new Error('Failed to fetch bookings');
        }
        const data = await response.json();
        console.log(data)
        console.log('bookibgs',bookings)
        setBookings(data);
      } catch (error) {
        console.error('Error fetching bookings:', error);
      }
    };

    fetchBookings();
  }, [user.data._id]);

  return (
    <section>
      <BookingList bookings={bookings} />

      <Container>
        <Row>
          <Col lg='12' className='mb-5'>
            <Subtitle subtitle={'Recommendations'} />
            <h2 className='featured__tour-title'>Our Best Featured Tours</h2>
          </Col>
          <FeaturedTourList />
        </Row>
      </Container>

      <NewsLetter />
    </section>
  );
};

export default Dashboard;
