const BookingList = () => {
  const { user } = useContext(AuthContext);
  const [bookings, setBookings] = useState([]);

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const response = await fetch(`${BASE_URL}/bookings/${user.data._id}`);
        if (!response.ok) {
          throw new Error('Failed to fetch bookings');
        }
        const data = await response.json();
        setBookings(data); // Assuming data is an array of booking objects
      } catch (error) {
        console.error('Error fetching bookings:', error);
      }
    };

    fetchBookings();
  }, [user.data._id]); // Ensure useEffect runs when user ID changes

  return (
    <section>
      <Container>
        <Row>
          <Col lg='6'>
            <Subtitle subtitle={'Booked Tours'} />
            {/* Render the list of bookings */}
            <ul>
              {bookings.map((booking) => (
                <li key={booking._id}>{/* Render booking details */}</li>
              ))}
            </ul>
          </Col>
        </Row>
      </Container>
    </section>
  );
};
