import React, { useState, useEffect } from 'react';
import { Container, Row, Col } from 'reactstrap';
import './form.css';
import { BASE_URL } from '../utils/config';

function UpdateForm() {
  const [image, setImage] = useState('');
  const [formData, setFormData] = useState({
    title: '',
    city: '',
    address: '',
    distance: '',
    price: '',
    maxGroupSize: '',
    desc: '',
    photo: null,
    featured: false,
  });
  const [objectId, setObjectId] = useState('');

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    const val = type === 'checkbox' ? checked : value;
    setFormData((prevData) => ({
      ...prevData,
      [name]: val,
    }));
  };

  function convertToBase64(e) {
    const file = e.target.files[0];
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = () => {
      const base64Result = reader.result;
      setImage(base64Result);
      setFormData((prevData) => ({
        ...prevData,
        photo: base64Result,
      }));
    };
    reader.onerror = (error) => {
      console.error('Error: ', error);
    };
  }

  const handleInputChange = (e) => {
    setObjectId(e.target.value);
  };

  const handleSubmit = async () => {
    console.log('ObjectId:', objectId);

    try {
      const response = await fetch(`${BASE_URL}/tours/${objectId}`);
      const result = await response.json();
      console.log('Retrieved tour:', result);
      setObjectId(result.data._id);
      console.log('Updated objectId:', objectId);
    } catch (error) {
      console.error('Error retrieving tour:', error);
      window.alert('Oops. Try Again');
    }
  };

  const handleUpdate = async () => {
    console.log('update ObjectId:', objectId);
  };

  useEffect(() => {
    if (objectId) {
      fetch(`${BASE_URL}/tours/${objectId}`)
        .then((response) => response.json())
        .then((result) => {
          setFormData(result.data);
        })
        .catch((error) => {
          console.error('Error retrieving tour:', error);
          window.alert('Oops. Try Again');
        });
    }
  }, [objectId]);

  return (
    <section>
      <Container>
        <Row>
          <Col>
            <div className="search__input">
              <input
                type="text"
                placeholder="Enter the Tour Id"
                value={objectId}
                onChange={handleInputChange}
              />
              <button className="search__btn" onClick={handleSubmit}>
                Get
              </button>
            </div>
          </Col>
        </Row>
      </Container>

      <Container>
        <Row>
          <Col>
            <form>
              <div className="form-group">
                <label>Photo</label>
                <input type="file" name="photo" onChange={convertToBase64} />
                {image && <img width={100} height={100} src={formData.photo} alt="" />}
              </div>
              <div className="form-group">
                <input
                  type="text"
                  placeholder="Title"
                  name="title"
                  value={formData.title}
                  onChange={handleChange}
                />
              </div>
              <div className="form-group">
                <input
                  type="text"
                  placeholder="City"
                  name="city"
                  value={formData.city}
                  onChange={handleChange}
                />
              </div>
              <div className="form-group">
                <input
                  type="text"
                  placeholder="Address"
                  name="address"
                  value={formData.address}
                  onChange={handleChange}
                />
              </div>
              <div className="form-group">
                <input
                  type="number"
                  placeholder="Distance"
                  name="distance"
                  value={formData.distance}
                  onChange={handleChange}
                />
              </div>
              <div className="form-group">
                <input
                  type="number"
                  placeholder="Price"
                  name="price"
                  value={formData.price}
                  onChange={handleChange}
                />
              </div>
              <div className="form-group">
                <input
                  type="number"
                  placeholder="Max - People"
                  name="maxGroupSize"
                  value={formData.maxGroupSize}
                  onChange={handleChange}
                />
              </div>
              <div className="form-group">
                <textarea
                  name="desc"
                  placeholder="Description"
                  value={formData.desc}
                  onChange={handleChange}
                />
              </div>
              <div className="form-group">
                <label>Featured</label>
                <input
                  type="checkbox"
                  name="featured"
                  checked={formData.featured}
                  onChange={handleChange}
                />
              </div>
              <button type="button" onClick={handleUpdate}>Submit</button>
            </form>
          </Col>
        </Row>
      </Container>
    </section>
  );
}

export default UpdateForm;
