import React, { useContext, useState, useEffect } from 'react';
import { Container, Row, Col } from 'reactstrap';
import NewsLetter from '../shared/Newsletter';
import FeaturedTourList from '../components/Featured-tours/FeaturedTourList';
import Subtitle from '../shared/Subtitle';
import BookingList from './BookingList'; // Import the BookingList component
import { AuthContext } from '../context/AuthContext';
import { BASE_URL } from '../utils/config';

BookingList = () => {
    const { user } = useContext(AuthContext);
    const [bookings, setBookings] = useState([]);
  
    useEffect(() => {
      const fetchBookings = async () => {
        try {
          const response = await fetch(`${BASE_URL}/bookings/${user.data._id}`);
          if (!response.ok) {
            throw new Error('Failed to fetch bookings');
          }
          const data = await response.json();
          setBookings(data); // Assuming data is an array of booking objects
        } catch (error) {
          console.error('Error fetching bookings:', error);
        }
      };
  
      fetchBookings();
    }, [user.data._id]); // Ensure useEffect runs when user ID changes
  
    return (
      <section>
        <Container>
          <Row>
            <Col lg='6'>
              <Subtitle subtitle={'Booked Tours'} />
              {/* Render the list of bookings */}
              <ul>
                {bookings.map((booking) => (
                  <li key={booking._id}>{/* Render booking details */}</li>
                ))}
              </ul>
            </Col>
          </Row>
        </Container>
      </section>
    );
  };
  

const Dashboard = () => {
  return (
    <section>
      <BookingList /> {/* Render the BookingList component */}
      <Container>
        <Row>
          <Col lg='12' className='mb-5'>
            <Subtitle subtitle={'Recommendations'} />
            <h2 className='featured__tour-title'>Our Best Featured Tours</h2>
          </Col>
          <FeaturedTourList />
        </Row>
      </Container>
      <NewsLetter />
    </section>
  );
};

export default Dashboard;
