import React from 'react';
import { Link } from 'react-router-dom';
import { BASE_URL } from '../utils/config';
import galleryImages from './../Gallery/images'; // Assuming you have the galleryImages array
import './gallery.css'

const genres = ['Adventure', 'Art and Culture', 'Beaches','Crafts','Cuisine','Festivals','Foe'];

const Gallery = () => {
    return (
        <div className="container">
            <div className="row">
                {genres.map((genre, index) => (
                    <div className="col-md-3 mb-3" key={index}>
                        <Link to={`${BASE_URL}/gallery/${genre}`} className="card">
                            <div className="card-body">
                                <h4>{genre}</h4>
                                <img src={galleryImages[index]} alt={genre} className="card-img-top" />
                            </div>
                        </Link>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Gallery;
