import React, { useState, useEffect } from 'react';
import { BASE_URL } from '../utils/config';
import { Container, Row, Col } from 'reactstrap';
import useFetch from '../hooks/useFetch'


function Table() {
  const [pageCount, setPageCount] = useState(0);
  const [page, setPage] = useState(0);

  const { data: tours, loading, error } = useFetch(`${BASE_URL}/tours?page=${page}`)
  const { data: tourCount } = useFetch(`${BASE_URL}/tours/search/getTourCount`)

  useEffect(() => {
    const pages = Math.ceil(tourCount / 8)
    setPageCount(pages);
    window.scrollTo(0, 0)
  }, [page, tourCount]);

  return (
    <>
      <section>
        <Container>
          {loading && <h4 className='text-center pt-5'>Loading.......</h4>}
          {error && <h4 className='text-center pt-5'>{error}</h4>}
          {!loading && !error && (
            <Row>
              <table >
                <thead>
                  <tr>
                    <th>S.No</th>
                    <th>Title</th>
                    <th>City</th>
                    <th>Address</th>
                    <th>Distance</th>
                    <th>Photo</th>
                    <th>Price</th>
                  </tr>
                </thead>
                <tbody>
                  {tours?.map((tour, index) => (
                    <tr key={tour._id}>
                      <td>{index + 1}</td> {/* Add index + 1 to start with 1-based serial number */}
                      <td>{tour.title}</td>
                      <td>{tour.city}</td>
                      <td>{tour.address}</td>
                      <td>{tour.distance}</td>
                      <td><img src={tour.photo} alt={tour.title} style={{ width: '100px', height: 'auto' }} /></td>
                      <br />
                      <td>{tour.price}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
              <Col lg='12'></Col>
              <Col lg='12'>
                <div className="pagination d-flex align-items-center justify-content-center mt-4 gap-3">
                  {[...Array(pageCount).keys()].map(number => (
                    <span key={number} onClick={() => setPage(number)} className={page === number ? 'active__page' : ''}>
                      {number + 1}
                    </span>
                  ))}
                </div>
              </Col>
            </Row>
          )}
        </Container>
      </section>
    </>
  );
}

export default Table;
